#include<bits/stdc++.h>
using namespace std;
const int N=10010<<1,INF=0x3f3f3f3f;

inline char nc(){
    static char buf[100000],*p1=buf,*p2=buf;
    return p1==p2&&(p2=(p1=buf)+fread(buf,1,100000,stdin),p1==p2)?EOF:*p1++;
}
inline int read(){
    char ch=nc();int sum=0;
    while(!(ch>='0'&&ch<='9'))ch=nc();
    while(ch>='0'&&ch<='9')sum=sum*10+ch-48,ch=nc();
    return sum;
}

struct E{
	int f,t,capa,flow,xt;
}e[N];

int h[N],cnt;

inline void adge(int f,int t,int capa){
	e[++cnt]=(E){f,t,capa,0,h[f]};h[f]=cnt;
	e[++cnt]=(E){t,f,0,0,h[t]};h[t]=cnt;
}

int d[N],cur[N],p[N],num[N];

int ISAP(int s,int t,int n){     			//ISAP 
	for(int i=0;i<=n;i++) cur[i]=h[i],d[i]=INF;
	int flow=0;
	queue<int>q;
	q.push(t);
	d[t]=0;
	while(!q.empty()){
		int u=q.front();q.pop();
		for(int i=h[u];~i;i=e[i].xt){
			if(d[e[i].t]==INF) num[d[e[i].t]=d[u]+1]++,q.push(e[i].t);
		}
	}
	for(int x=s;d[s]<n;){
		if(x==t){
			int a=INF;
			for(;x!=s;x=e[p[x]].f) a=min(a,e[p[x]].capa-e[p[x]].flow);
			for(x=t;x!=s;x=e[p[x]].f) e[p[x]].flow+=a,e[p[x]^1].flow-=a;
			flow+=a;
		}
		bool ok=0;
		for(int i=cur[x];~i;i=e[i].xt){
			if(e[i].capa>e[i].flow&&d[e[i].t]==d[x]-1){
				ok=1;
				cur[x]=i;
				x=e[i].t;
				p[x]=i;
				break;
			}
		}
		if(!ok){
			int m=n-1;
			for(int i=cur[x]=h[x];~i;i=e[i].xt){
				if(e[i].capa>e[i].flow) m=min(m,d[e[i].t]);
			}
			if(--num[d[x]]==0) break;
			num[d[x]=m+1]++;
			if(x!=s) x=e[p[x]].f;
		}
	}
	return flow;
}

int pos[110],od[110],ev[110],c1,c2;

bool isp(int n){
	if(n==0||n==1) return 0;
	int m=sqrt(n);
	for(int i=2;i<=m;i++)
		if(n%i==0) return 0;
	return 1;
}

inline void add(int x){
	if(x&1) od[++c1]=x;
	else ev[++c2]=x;
}
int main(){
	memset(h,cnt=-1,sizeof(h));
	int n;
	n=read();
	for(int i=1;i<=n;i++){
		pos[i]=read();
		if(pos[i-1]+1!=pos[i]||i==1){		
			if(i>1) add(pos[i-1]+1);
			add(pos[i]);
		}
	}
	add(pos[n]+1);
	for(int i=1;i<=c1;i++){
		for(int j=1;j<=c2;j++){
			if(isp(abs(od[i]-ev[j]))){
				adge(i,j+c1,1);
			}
		}
	}
	for(int i=1;i<=c1;i++) adge(0,i,1);
	for(int i=1;i<=c2;i++) adge(i+c1,c1+c2+1,1);
	int ans=ISAP(0,c1+c2+1,c1+c2+1);
	int t=ans;
	ans=ans+(c1-t)/2*2;
	ans=ans+(c2-t)/2*2;
	if((c1-t)%2) ans+=3;
	printf("%d\n",ans);
	return 0;
}

