		mx=lst+1;
	}
	v.push_back(lst+1);
	f[1]=true;
	for(int i=2;i<=mx;i++)
	{
		if(!f[i])prm[++sz]=i;
		for(int j=1;j<=sz;j++)
		{
			if(prm[j]*i>mx)break;
			f[prm[j]*i]=true;
			if(i%prm[j]==0)break;
		}
