	int mxn=10000001;
	rep(i,1,n) aa[i]=read(),mp[aa[i]]=1;
	//rep(i,1,n) if (mp[a[i]-1]==mp[a[i]]) b[i]=0; else b[i]=1;
	rep(i,1,mxn) if (mp[i]!=mp[i-1]) b[++m]=i; 
	rep(i,2,mxn) {
		if (!vis[i]) pri[++ptot]=i,vis[i]=1,pmp[i]=1;
		rep(j,1,ptot) {
			if (i*pri[j]>mxn) break;
			vis[i*pri[j]]=1;
		}
	}
//	rep(i,1,m) printf("%d ",b[i]);
	n=m;
