#define MAXN 10000005

int Abs (int x){return x > 0 ? x : -x;}

int tot;
int prime[MAXN];

bool vis[MAXN];

void Prime (int n)
{
	vis[1] = 1;
	for (Int i = 2;i <= n;++ i)
	{
		if (!vis[i]) prime[++ tot] = i;
		for (Int j = 1;j <= tot && i * prime[j] <= n;++ j)
		{
			vis[i * prime[j]] = 1;
			if (i % prime[j] == 0) break;
		}
	}
}

bool check (int x){return !vis[x];}

bool rev[MAXN];

int read ()
{
	int x = 0;char c = getchar();int f = 1;
	while (c < '0' || c > '9'){if (c == '-') f = -f;c = getchar();}
	while (c >= '0' && c <= '9'){x = (x << 3) + (x << 1) + c - '0';c = getchar();}
	return x * f;
}

void write (int x)
{
	if (x < 0){x = -x;putchar ('-');}
	if (x > 9) write (x / 10);
	putchar (x % 10 + '0');
}

vector <int> s1,s2;


vector <int> G[MAXN];

int my[MAXN];
bool visit[MAXN];

int dfs (int u)
{
	for (Int i = 0;i < G[u].size();++ i)
	{
		int v = G[u][i];
		if (visit[v]) continue;
		visit[v] = 1;
		if (my[v] == -1 || dfs (my[v]))
		{
			my[v] = u;
			return 1;
		}
	}
	return 0;
}

int Hungry ()
{
	int cnt = 0;
	memset (my,-1,sizeof (my));
	for (Int i = 0;i < s1.size();++ i)
	{
		memset (visit,0,sizeof (visit));
		cnt += dfs (i);
	}
	return cnt;
}

signed main()
{
	int n = read ();
	int Maxn = 0;
	for (Int i = 1;i <= n;++ i)
	{
		int x = read ();
		rev[x] ^= 1;
		Maxn = max (Maxn,x + 1);
	}
	Prime (Maxn);
	for (Int i = 1;i <= Maxn;++ i)
		if (rev[i] != rev[i - 1])
		{
			if (i & 1)
				s1.push_back(i);
			else 
				s2.push_back(i);
		}
	for (Int i = 0;i < s1.size();++ i)
		for (Int j = 0;j < s2.size();++ j)
			if (check (Abs (s1[i] - s2[j])))
				G[i].push_back(j); 
	int Ans = Hungry ();
	write (Ans + (s1.size() - Ans) / 2 * 2 + (s2.size() - Ans) / 2 * 2 + s1.size() % 2 * 3),putchar ('\n'); 
	return 0; 
}

