			X.push_back(x + 1);
		}
		else {
			X.push_back(x);
			X.push_back(x + 1);
		}
	}
	int E = 0;
	int O = 0;
	for (int x : X) {
		if (x % 2 == 0) {
			E++;
		}
		else {
			O++;
		}
	}

	C.resize(X.size()+2, vi(X.size()+2, 0));
	for (int i = 0; i < X.size(); i++) {
