int Pow(int a,int b,int p)
{
	int ans=1;
	for(;b;b>>=1,a=1ll*a*a%p)
		if(b&1) ans=1ll*ans*a%p;
	return ans;
}

bool isprime(int n)
{
	if(n==2) return true;
	if(n<2||!(n&1)) return false;
	int t=0;
	int a,x,y,u=n-1;
	while(!(u&1)) t++,u>>=1;
	for(int i=0;i<10;i++)
	{
		a=rand()%(n-1)+1;
		x=Pow(a,u,n);
		for(int j=0;j<t;j++,x=y)
		{
			y=1ll*x*x%n;
			if(y==1&&x!=1&&x!=n-1) return false;
		}
		if (x!=1) return false;
	}
	return true;
}

