const int MAX = 10000000;
int min_factor[MAX+1];
vector<int>prime;
void make_prime() {
    for (int i=2; i<=MAX; i+=2) min_factor[i] = 2;
    for (int i=3; i<=MAX; i+=3) if (!min_factor[i]) min_factor[i] = 3;
    for (int i=5, d=2; i*i<=MAX; ) {
	if (!min_factor[i]) {
	    min_factor[i] = i;
	    for (int j=i*i; j<=MAX; j+=i)
		if (!min_factor[j]) min_factor[j] = i;
	}
	i += d; d = 6 - d;
    }
    for (int i=2; i<=MAX; i++) {
	if (min_factor[i]==0) min_factor[i] = i;
	if (min_factor[i]==i) prime.push_back(i);
    }
}
bool is_prime(LL n) {
    if (n<=MAX) return min_factor[n] == n;
    for (int i=0; (LL)prime[i]*prime[i]<=n; i++)
	if (n%prime[i]==0) return false;
    return true;
}



struct Matching2 { // for bipartite graph
    vector<int>matchM, matchW, use;
    int ans;
    bool dfs(const VI G[], int v) {
	if (use[v]) return false;
	use[v] = true;
	for (int i=0; i<(int)G[v].size(); i++) {
	    int u = G[v][i];
	    if (matchW[u] == -1 || dfs(G, matchW[u])) {
		matchM[v] = u;
		matchW[u] = v;
		return true;
	    }
	}
	return false;
    }
    Matching2(const VI G[], int sizeM, int sizeW): matchM(sizeM, -1), matchW(sizeW, -1), ans(0) {
	use = vector<int>(sizeM, 0);
	for (int i=0; i<sizeM; i++) {
	    fill(use.begin(), use.end(), 0);
	    if (dfs(G, i)) ans++;
	}
    }
};

int N;
int X[111];
VI G[211];

void MAIN() {
    make_prime();
    scanf("%d", &N);
    REP (i, N) scanf("%d", X+i);

    VI S;
    REP (i, N) {
	if (i == 0 || X[i-1] + 1 < X[i]) {
	    S.push_back(X[i]);
	}
	if (i == N-1 || X[i] + 1 < X[i+1]) {
	    S.push_back(X[i]+1);
	}
    }
    VI E, O;
    EACH (e, S) {
	if (*e & 1) O.push_back(*e);
	else E.push_back(*e);
    }

    REP (i, O.size()) REP (j, E.size()) if (abs(O[i] - E[j]) > 2 && is_prime(abs(O[i] - E[j]))) {
	G[i].push_back(j);
    }
    Matching2 M(G, O.size(), E.size());

    int ans;
    if ((O.size() ^ M.ans) & 1) {
	ans = E.size() + O.size() - M.ans + 1;
    } else {
	ans = E.size() + O.size() - M.ans;
    }

    printf("%d\n", ans);
}

int main() {
    int TC = 1;
//    scanf("%d", &TC);
    REP (tc, TC) MAIN();
    return 0;
}

