void Get_prime(int m)
{
	notprim[0] = notprim[1] = 1;
	For(i, 2, m)
	{
		if(!notprim[i])
			prime.push_back(i);
		int len = prime.size();
		For(j, 0, len - 1)
		{
			if(prime[j] * i > m)
				break;
			notprim[prime[j] * i] = 1;
			if(i % prime[j] == 0)
				break;
		}
	}
}

namespace Match
{
	const int N = 1e3 + 10;
	int to[N], head[N], nxt[N], e;
	int pre[N], vis[N], G[N], O[N], now, sum;
	void add(int x, int y)
	{
		to[++ e] = y;
		nxt[e] = head[x];
		head[x] = e;
	}
	bool dfs(int x)
	{
		go(x, i)
		{
			if(vis[to[i]] != now)
			{
				vis[to[i]] = now;
				if(!pre[to[i]] || dfs(pre[to[i]]))
				{
					pre[to[i]] = x;
					return 1;
				}
			}
		}
		return 0;
	}
	int match()
	{
		int res = 0;
		for(now = 1; now <= G[0]; ++ now)
			res += dfs(now);
		return res;
	}
	void solve()
	{
		int res = 0;
		For(i, 0, SZ(num) - 1)
			if(num[i] & 1)
				G[++ G[0]] = num[i];
			else
				O[++ O[0]] = num[i];
		For(i, 1, G[0])
			For(j, 1, O[0])
				if(!notprim[abs(G[i] - O[j])])
					add(i, j);
		res += match();
		memset(vis, 0, sizeof(vis));
		G[0] -= res, O[0] -= res;
		res += G[0] / 2 * 2, G[0] %= 2;
		res += O[0] / 2 * 2, O[0] %= 2;
		if(G[0] * O[0])
			res += 3;
		cout << res << endl;
	}
}

