vector <int> edge[220],v2;

bool fin(int u) {
    if (fre[u]=wrong) return false;
    fre[u]=wrong;
    for (int j=0;j<edge[u].size();j++) {
        int v=edge[u][j];
        if (p[v]=0 || fin(p[v])) {
            p[u]=v;
            p[v]=u;
            return true;
        }
    }
    return false;
}

int main() {
  //  freopen("in.txt","r",stdin);
    ios_base::sync_with_stdio(0); cin.tie(0);
    isprime[1]=1;
    for (int i=2;i<=maxn;i++)
        if (isprime[i]==0) {
            for (int j=i+i;j<=maxn;j+=i) isprime[j]=1;
        }
    cin>>n;
    for (int i=1;i<=n;i++) {
        int x;
        cin>>x;
        fre[x]=1;
    }
    even=0; odd=0; wrong=2;
    int id=1;
    while (id<=maxn) {
        if (fre[id]==0) id++;
        else {
            v2.push_back(id);
            if (id%2==0) even++; else odd++ ;
            id++;
            while (id<=maxn && fre[id]==1) id++;
            v2.push_back(id);
            if (id%2==0) even++; else odd++ ;
        }
    }
    // for (int i=0;i<v2.size();i++) cout<<v2[i]<<" ";
   //  cout<<endl;
   // cout<<" " <<even<<" "<<odd<<endl;
    for (int i=0;i<v2.size();i++)
        for (int j=i+1;j<v2.size();j++)
            if (isprime[ v2[j] - v2[i] ]==0) {
                edge[i+1].push_back(j+1);
                edge[j+1].push_back(i+1);
            }
    int cnt=0;
    for (int i=0;i<v2.size();i++) {
        wrong++;
        cnt+=(fin(i));
    }
   // cout<<cnt<<" " <<even<<" "<<odd<<endl;
    cout<< cnt + ((even-cnt)/2 + (odd-cnt)/2) * 2 + (even%2)*3;
}

