int p[inf],ckp[inf],pcnt;
const int lim=10000000;

void pre_work(void){
    ckp[0]=ckp[1]=1;
    for (int i=2;i<=lim;i++){
        if (!ckp[i]){
            p[++pcnt]=i;
        }
        for (int j=1;j<=pcnt && p[j]*i<=lim;j++){
            ckp[p[j]*i]=1;
            if (i%p[j]==0){
                break;
            }
        }
    }
    return;
}

int S,T;

struct edge{
    int nxt,y,s;
}e[infm*2];
int ecnt=1,head[infm],h[infm];

void addedge(int x,int y,int s){
    ecnt++;
    e[ecnt].nxt=head[x];
    e[ecnt].y=y;
    e[ecnt].s=s;
    head[x]=ecnt;
    return;
}

void ae(int x,int y,int s){
    addedge(x,y,s);
    addedge(y,x,0);
    return;
}

queue <int> q;
int dep[inf];

int dfs(int u,int flow){
    if (!flow || u==T){
        return flow;
    }
    int ans=0;
    for (int i=h[u];i;i=e[i].nxt){
        h[u]=i;
        int v=e[i].y,s=e[i].s,now;
        if (dep[v]!=dep[u]+1){
            continue;
        }
        now=dfs(v,min(flow,s));
        if (now){
            ans+=now;
            flow-=now;
            e[i].s-=now;
            e[i^1].s+=now;
            if (!flow){
                break;
            }
        }
    }
    return ans;
}

int dinic(void){
    int ans=0;
    while (1){
        while (!q.empty()){
            q.pop();
        }
        memcpy(h,head,sizeof(head));
        memset(dep,-1,sizeof(dep));
        dep[S]=0;
        q.push(S);
        while (!q.empty()){
            int u=q.front(),v,s;
            q.pop();
            for (int i=head[u];i;i=e[i].nxt){
                v=e[i].y,s=e[i].s;
                if (dep[v]<0 && s){
                    dep[v]=dep[u]+1;
                    q.push(v);
                }
            }
        }
        if (dep[T]<0){
            break;
        }
        ans+=dfs(S,INF);
    }
    return ans;
}

int n;
