    rd(n)
    int x,mx=0;
    for (int i=1;i<=n;i++){
        rd(x)
        a[x]++;
        mx=max(mx,x);
    }
    int cntl=0,cntr=0;
    for (int i=1;i<=mx+1;i++){
        if (a[i]!=a[i-1]){
            if (i&1){
                L[++cntl]=i;
            }
            else{
                R[++cntr]=i;
            }
        }
    }
    S=0,T=cntl+cntr+1;
    for (int i=1;i<=cntl;i++){
        ae(S,i,1);
    }
    for (int i=1;i<=cntr;i++){
        ae(i+cntl,T,1);
    }
    for (int i=1;i<=cntl;i++){
        for (int j=1;j<=cntr;j++){
            if (!ckp[abs(L[i]-R[j])]){
                ae(i,j+cntl,1);
            }
