/*  */
struct edge {int to, cap ,rev; };

/*  */
vector<edge> G[MAX_V];
/* DFS */
bool used[MAX_V];

/* fromtocap */
/*  */
void add_edge(int from, int to, int cap){
    G[from].push_back((edge){to, cap, (int)G[to].size()});
    G[to].push_back((edge){from, 0, (int)G[from].size() - 1});    
}

/* dfs */
int dfs(int v, int t, int f){
    if(v==t) return f;
    used[v] = true;
    for(int i=0; i<G[v].size(); i++){
        edge &e = G[v][i];
        if(!used[e.to] && e.cap>0){
            int d = dfs(e.to, t, min(f, e.cap));
            if(d>0){
                e.cap -= d;
                G[e.to][e.rev].cap += d;
                return d;
            }
        }
    }
    return 0;
}

int max_flow(int s, int t){
    int flow = 0;
    for(;;){
        memset(used, 0, sizeof(used));
        int f = dfs(s, t, INF);
        if(f==0) return flow;
        flow += f;
    }
}

bool diffIsPrime(int a, int b) {
    if(a > b) swap(a, b);
    a = b - a;
    if(a <= 2) return false;
    repn(i, 2, sqrt(a)+1) if(a % i == 0) return false;

    return true;
}

