    vector<int> odd, even;
    for(auto it = mp.begin(); it != mp.end(); it++) {
        if(it->second % 2 == 1) {
            if(it->first % 2 == 0) {
                even.push_back(it->first);
            } else {
                odd.push_back(it->first);
            }
        }
    }

    int start = 0, end = 201;
    rep(i, even.size()) add_edge(start, i+1, 1);
    rep(i, odd.size()) add_edge(i+101, end, 1);
