#endif

    vector<SQWORD> vsqCOdd;
    vector<SQWORD> vsqCEven;

    unordered_map<SQWORD, SQWORD> mapCEven, mapCOdd;

    SQWORD sqIdx = 1;
    for (auto b: vsqB) {
        if (0 == b % 2) {
            vsqCEven.emplace_back(b);
            mapCEven[b] = sqIdx;
        } else {
            vsqCOdd.emplace_back(b);
            mapCOdd[b] = sqIdx;
        }
        sqIdx++;
    }

