struct Edge {
    int from, to, f, c, w;
};
vector < Edge >  edges;
vector < int > g[N];
int n, d[N], f[N], b[N][N];
int p[N];
void add(int a, int b, int c, int w){
    Edge e;
    e.from = a; e.to = b; e.c = c; e.w = w; e.f = 0;
    edges.pb(e);
  //  cout << a << " " << b << " " << c << " " << w << endl;
    g[a].pb(edges.size() - 1);
    e.w = -e.w; e.c = 0; swap(e.from, e.to);
    edges.pb(e);
    g[b].pb(edges.size() - 1);
}
bool spfa(int s, int t){
    for (int i = 1; i < N; i++){
        d[i] = 1e9;
        f[i] = 0;
    }
    queue < int > q;
    q.push(s);
    d[s] = 0;
    f[s] = 1;
    while(!q.empty()){
        int v = q.front();
        q.pop();
        f[v] = 0;
        for (int i = 0; i < g[v].size(); i++){
            Edge e = edges[g[v][i]];
            if (e.f < e.c && d[e.to] > d[v] + e.w){
                p[e.to] = g[v][i];
                d[e.to] = d[v] + e.w;
                if (!f[e.to])
                    q.push(e.to);
                f[e.to] = 1;
            }
        }
    }
    return d[t] != 1e9;
}
int flow(int s, int t){
    int cost = 0;
    while(spfa(s, t)){
        int v = t;
        while(v != s && v != 0){
            auto k = p[v];
            edges[k].f++;
            edges[k ^ 1].f--;
            cost += edges[k].w;
            v = edges[k].from;
        }
    }
    return cost;
}
int mx[N], mn[N];
int ff(int x){
    if (x == 2) return 2;
    if (x == 1) return 3;
    int pr = 1;
    for (int i = 2; i * i <= x; i++){
        if (x % i == 0) pr = 0;
    }
    if (pr) return 1;
    return 2 + (x % 2);
}
int usedd[N];
vector < int > gr[N];
void dfss(int v){
    usedd[v] = 1;
    for (auto u: gr[v]){
        if (usedd[u]) continue;
        dfss(u);
    }
}
