void pre(int N) {
	for(int i = 2; i <= N; ++i) {
		if(!ip[i]) p[++cnt] = i;
		for(int j = 1; j <= cnt && p[j]*i <= N; ++j) {
			ip[p[j]*i] = 1;
			if(i % p[j] == 0) break;
		}
	}
}
bool vis[MAXN];
bool dfs(int u) {
	for(int v = 1; v <= n; ++v)
		if(w[u][v] && !vis[v]) {
			vis[v] = 1;
			if(!match[v] || dfs(match[v])) {
				match[v] = u;
				match[u] = v;
				return 1;
			}
		}
	return 0;
}
void maxmatch() {
	for(int i = 1; i <= n; ++i)
		if(!used[i] && (x[i]&1)) {
			memset(vis,0,sizeof vis);
			dfs(i);
		}
}
