int dfs(int x,int y){
    int flow=0;
    if(x==y) return INF;
    chk[x]=1;
    for(int i=0;i<conn[x].size();i++) if(chk[conn[x][i].first]==0&&conn[x][i].second>0){
        int t=dfs(conn[x][i].first,y);
        if(t>0){
            flow=min(t,conn[x][i].second);
            seq.push_back(x);
            break;
        }
    }
    chk[x]=0;
    return flow;
}
int fflow(int x,int y){
    seq.clear(); seq.push_back(y);
    int flow=dfs(x,y);
    for(int i=0;i<seq.size()-1;i++) {
        int a=seq[i+1],b=seq[i];
        for(int j=0;j<conn[a].size();j++) if(conn[a][j].first==b) conn[a][j].second-=flow;
        for(int j=0;j<conn[b].size();j++) if(conn[b][j].first==a) conn[b][j].second+=flow;
    }
    return flow;
}
