int read(){
	int x=0,f=1;char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-') f=-1;ch=getchar();}
	while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();}
	return f*x;
}
void add_edge(int from,int to){
	edge[++num_edge].nxt=head[from];
	edge[num_edge].to=to;
	head[from]=num_edge;
}
bool find(int x){
	for(int i=head[x];i;i=edge[i].nxt){
		if(vis[edge[i].to]!=tim){
			vis[edge[i].to]=tim;
			if(!lk[edge[i].to]||find(edge[i].to)){
				lk[edge[i].to]=x;
				return 1;
			}
		}
	}
	return 0;
}
bool check(int x){
	if(x==1) return 0;
	for(int i=2;i*i<=x;++i){
		if(x%i==0) return 0;
	}
	return 1;
}
void solve(int x){
	if(x&1) odd[++odd_num]=x;
	else even[++even_num]=x;
}
int main(){
