		if(b[i]+1!=b[i+1]) solve(b[i]+1);
	}
	for(int i=1;i<=odd_num;++i){
		for(int j=1;j<=even_num;++j){
			if(check(abs(even[j]-odd[i]))) add_edge(i,j+odd_num);
		}
	}
	for(int i=1;i<=odd_num;++i){
		tim++;
		if(find(i)) k++;
	}
	ans=k+((even_num-k)/2+(odd_num-k)/2)*2+((odd_num-k)%2)*3;
	printf("%d",ans);
	return 0;
}

