int N,M;
const int MAX_N = 111;
int a[MAX_N],b[MAX_N*2],sz;
vector<int> odd,even;

vector<int> path[MAX_N*2];
bool visit[MAX_N*2];
int match[MAX_N*2];

bool dfs(int x){
	if(visit[x])return false;
	visit[x]=1;
	for(int i=0;i<path[x].size();i++){
		int y=path[x][i];
		if(match[y]<0){
			match[y]=x;
			return true;
		}else if(dfs(match[y])){
			return true;
		}
	}
	return false;
}

bool isPrime(int x){
	if(x==1)return false;
	if(x%2==0)return false; // 2 is exception
	for(int i=3;i*i<=x;i+=2){
		if(x%i==0)return false;
	}
	return true;
}

int main(){
	scanf("%d",&N);
	for(int i=0;i<N;i++){
		scanf("%d",&a[i]);
	}

	b[sz++]=a[0];
	for(int i=1;i<N;i++){
		if(a[i-1]+1<a[i]){
			b[sz++]=a[i-1]+1;
			b[sz++]=a[i];
		}
	}
	b[sz++]=a[N-1]+1;

	for(int i=0;i<sz;i++){
		if(b[i]%2==0)even.push_back(b[i]);
		else odd.push_back(b[i]);
	}

	for(int i=0;i<even.size();i++){
		for(int j=0;j<odd.size();j++){
			if(isPrime(abs(even[i]-odd[j]))){
				path[i].push_back(j);
			}
		}
	}

	for(int i=0;i<odd.size();i++){
		match[i]=-1;
	}

	int cnt=0;
	for(int i=0;i<even.size();i++){
