int edge[300][300];

int n, x;
map<int, int> cnt;
vector<int> odd, even;

void add_edge(int u, int v, int f){
  edge[u][v] = f;
}


bool come[300];
int flow(int s, int t){
  if(come[s])return 0;
  come[s] = true;
  if(s == t)return 1;
  for(int i = 0;i < 300;i++){
    if(edge[s][i] == 0)continue;
    if(flow(i, t)){
      edge[s][i]--;
      edge[i][s]++;
      return 1;
    }
  }
  return 0;
}

int max_flow(int s, int t){
  int res = 0;
  while(flow(s, t)){
    res++;
    for(int i = 0;i < 300;i++)come[i] = false;
  }
  return res;
}


int main(){
