namespace Network{
	const int maxn=1e3+5,maxm=1e6+5,inf=1e9;
	int S,T,tot;
	int head[maxn],from[maxm],des[maxm],nxt[maxm],f[maxm];
	int cur[maxn],h[maxn],pre[maxn],d[maxn];
	void init(int _S,int _T){
		memset(head,0,sizeof head);tot=1;
		S=_S;T=_T;
	}
	void adde(int x,int y,int z){
		from[++tot]=x;des[tot]=y;f[tot]=z;nxt[tot]=head[x];head[x]=tot;
		from[++tot]=y;des[tot]=x;f[tot]=0;nxt[tot]=head[y];head[y]=tot;
	}
	int isap(int n){
		int u=S,maxflow=0;
		rep(i,1,n)cur[i]=head[i],d[i]=pre[i]=0;
		h[0]=n;
		while(d[S]<n){
			if(u==T){
				int re=S,minflow=inf;
				for(;u!=S;u=from[pre[u]])if(f[pre[u]]<minflow)minflow=f[pre[u]],re=from[pre[u]];
				u=T;maxflow+=minflow;
				for(u=T;u!=S;u=from[pre[u]])f[pre[u]]-=minflow,f[pre[u]^1]+=minflow;
				u=re;
			}
			int flag=0;
			Rep2(p,u){
				if(f[p]&&d[des[p]]==d[u]-1){
					flag=1;u=des[p];pre[u]=p;
					break;
				}
				cur[u]=p;
			}
			if(flag)continue;
			cur[u]=head[u];int mind=n;
			Rep2(p,u)if(f[p]&&d[des[p]]<mind)mind=d[des[p]],cur[u]=p;
			h[d[u]]--;if(!h[d[u]])break;
			d[u]=mind+1;h[d[u]]++;
			if(u!=S)u=from[pre[u]];
		}
		return maxflow;
	}
}
