bool sko[N];
bool not_prime[E];

void eratos() {
	for (int i = 2; i < E; i ++) {
		if (not not_prime[i]) {
			for (int j = 2 * i; j < E; j += i) {
				not_prime[j] = true;
			}
		}
	}
}

bool dfs(int x) {
	if (vis[x]) return false;
	vis[x] = true;
	for (int y: G[x]) {
		if (mate[y] == 0 || dfs(mate[y])) {
			mate[y] = x;
			sko[x] = true;
			return true;
		}
	}
	return false;
}

int main() {
	int n;
	scanf("%d", &n);
	eratos();

	vector<int> data;
	for (int i = 0; i < n; i ++) {
		int a;
		scanf("%d", &a);
		data.push_back(a);
	}
	sort(data.begin(), data.end());
	int last = -1;
	vector<int> tokens;
	for (int d: data) {
		if (last < d) {
			if (last != -1) {
				tokens.push_back(last);
			}
			tokens.push_back(d);
		}
		last = d + 1;
	}
	tokens.push_back(last);
	vector<int> nodes[2];
	for (int t: tokens) {
		nodes[t % 2].push_back(t);
	}
	for (int i = 0; i < tokens.size(); i ++) {
// 		printf("%d ", tokens[i]);
	}
// 	printf("tokens \n");
	not_prime[2] = true;
	not_prime[1] = true;
	for (int i = 0; i < nodes[0].size(); i ++) {
		for (int j = 0; j < nodes[1].size(); j ++) {
			if (not not_prime[abs(nodes[0][i] - nodes[1][j])]) {
				G[i+1].push_back(j+1);
			}
		}
	}
	bool still = true;
	int wynik = 0;
	while(still) {
		still = false;
		for (int i = 0; i <= nodes[0].size(); i ++) {
			vis[i] = false;
		}
		for (int i = 1; i <= nodes[0].size(); i ++) {
			if (sko[i]) continue;
			if (dfs(i)) {
				wynik ++;
				still = true;
			}
		}
	}
	printf("%d\n", wynik + 2 * ((tokens.size() - 2 * wynik) / 2) + (nodes[0].size() - wynik) % 2);
}

