const int N = 110;

struct MAX_FLOW
{
	static const int N = 210,M = N * N;
	EE(0);
	int S,T;
	queue<int> q;int dis[N],cur[N];
	bool BFS()
	{
		memset(dis,-1,sizeof(dis));
		q.push(S),dis[S] = 0;
		while(!q.empty())
		{
			int u = q.front();q.pop();
			for(int i = cur[u] = head[u];i;i = e[i].nxt)
			{
				int v = e[i].to;
				if(!~dis[v] && e[i].w) q.push(v),dis[v] = dis[u] + 1;
			}
		}return ~dis[T];
	}
	int DFS(int u,int exp)
	{
		if(u == T) return exp;
		int flow = 0,f;
		for(int &i = cur[u];i;i = e[i].nxt)
		{
			int v = e[i].to;
			if(e[i].w && dis[v] == dis[u] + 1)
			{
				f = DFS(v,min(exp,e[i].w));
				exp -= f,flow += f;
				e[i].w -= f,e[i ^ 1].w += f;
				if(!exp) break;
			}
		}return flow;
	}
	int Dinic()
	{
		int maxflow = 0,f;
		while(BFS()) while(f = DFS(S,INF)) maxflow += f;
		return maxflow;
	}
}G;

