int main()
{
	init(10000000);
	n = read();for(int i = 1;i <= n;i++) a[i] = read();sort(a + 1,a + 1 + n);
	for(int i = 1;i <= n;i++)
	{
		if(a[i - 1] + 1 != a[i] || i == 1) push(a[i]);
		if(a[i + 1] - 1 != a[i]) push(a[i] + 1);
	}
	G.S = 0,G.T = cntl + cntr + 1;
	for(int i = 1;i <= cntl;i++) G.add_edge(G.S,i,1);
	for(int i = 1;i <= cntr;i++) G.add_edge(i + cntl,G.T,1);
	for(int i = 1;i <= cntl;i++) for(int j = 1;j <= cntr;j++)
		if(!vis[abs(L[i] - R[j])]) G.add_edge(i,j + cntl,1);
	int maxflow = G.Dinic();
	int ans = maxflow;
	ans += (cntl - maxflow) / 2 * 2 + (cntr - maxflow) / 2 * 2;
	ans += ((cntl - maxflow) & 1) * 3;
	fprint(ans);
}

