            int x=p.front();p.pop();
            for(int i=hd[x];i;i=e[i].nxt){
                int y=e[i].to,v=e[i].val;
                if(!dep[y]&&v){
					dep[y]=dep[x]+1;
                    p.push(y);
                }
            }
        }
        if(dep[T]) return 1;
        return 0;
    }
    int dfs(int x,int flow){
        if(x==T||flow<=0) return flow;
        int rest=0;
