#include "bits/stdc++.h"
using namespace std;
const int N = 105;
const int M = 1e7 + 77;
int n;
int x[N];
bool p[M];
int nxt[M + 1];
void sieve(){
	for(int i = 2 ; i * i < M ; ++i){
		if(!p[i]){
			for(int j = i * i ; j < M ; j += i){
				p[j] = 1;
			}
		}
	}
	nxt[M] = 0;
	for(int i = M - 1 ; i >= 3 ; --i){
		if(p[i]){
			nxt[i] = nxt[i + 1];
		}
		else{
			nxt[i] = i;
		}
	}
	nxt[2] = 3;
	nxt[1] = 3;
	p[2] = 1;
	p[1] = 1;
	p[0] = 1;
}
inline int cost(int size){
	if(size <= 0){
		return 0;
	}
	if(p[size] == 0){
		return 1;
	}
	if(size & 1){
		return 3;
	}
	return 2;
}
int dp[N][1000];
int solve(int pos , int back){
	if(pos > n){
		return cost(back);
	}
	if(dp[pos][back] != -1){
		return dp[pos][back];
	}
	int res = 1e9;
	if(back){
		res = min(res , solve(pos , 0) + cost(back));
		int extra = cost(x[pos] - 1 - x[pos - 1] - back);
		for(int i = pos ; i <= n ; ++i){
			res = min(res , solve(i + 1 , 0) + extra + cost(x[i] - x[pos - 1]));
			int siz = x[i] - x[pos - 1];
			int p = nxt[siz];
			if(x[pos - 1] + 1 + p - 1 < x[i + 1]){
				res = min(res , solve(i + 1 , x[pos - 1] + 1 + p - 1 - x[i]) + p + extra + 1);
			}
			if(i < n){
				extra += cost(x[i + 1] - x[i] - 1);
			}
		}
	}
	else{
		int extra = 0;
		for(int i = pos ; i <= n ; ++i){
			res = min(res , solve(i + 1 , 0) + extra + cost(x[i] - x[pos] + 1));
			int siz = x[i] - x[pos] + 1;
			int p = nxt[siz];
			if(x[pos] + p - 1 < x[i + 1]){
				res = min(res , solve(i + 1 , x[pos] + p - 1 - x[i]) + p + extra + 1);
			}
			if(i < n){
				extra += cost(x[i + 1] - x[i] - 1);
			}
		}
	}
	return dp[pos][back] = res;
}
int main(){
	sieve();
	scanf("%d" , &n);
	for(int i = 1 ; i <= n ; ++i){
		scanf("%d" , x + i);
	}
	x[n + 1] = 1e8 + 8;
	sort(x + 1 , x + 1 + n);
	memset(dp , -1 , sizeof(dp));
	printf("%d\n" , solve(1 , 0));
}

