inline int Abs(int x) {
	return x >= 0 ? x : -x;
}

inline void Getprime() {
	Pr[0] = Pr[1] = true;
	for (int i = 2; i <= M; ++i) {
		if (!Pr[i]) p[++tot] = i;
		for (int j = 1; j <= tot && i * p[j] <= M; ++j) {
			Pr[i * p[j]] = true/* false */;
			if (i % p[j] == 0) break;
		}
	}
	Pr[2] = true;
}

bool Find(int x) {
	for (int j = 1; j <= cnt1; ++j)
		if (G[x][j] && (!vis[j])) {
			vis[j] = true;
			if ((!bel[j]) || (Find(bel[j]))) {
				bel[j] = x;
				return true;
			}
		}
	return false;
}

inline void Hungary() {
	for (int i = 1; i <= cnt0; ++i) {
		memset(vis, false, sizeof(vis));
		if (Find(i)) ++fsum;
	}
}

