			vis[i*prm[j]]=1;
			if(i%prm[j]==0) break;
		}
	}

	s=totl+totr+1,t=s+1;
	memset(h,-1,sizeof(h));
	rep(i,1,totl) rep(j,1,totr) if(!vis[abs(L[i]-R[j])]) addedge(i,j+totl);
	rep(i,1,totl) addedge(s,i);
	rep(i,1,totr) addedge(i+totl,t);
	memcpy(cur,h,sizeof(h));
	for(g[0]=t; dis[s]<t; ans+=isap(s,INF));
	printf("%d\n",ans+(totl-ans>>1)*2+(totr-ans>>1)*2+((totl-ans)&1)*3);
	return 0;
}

