inline bool is_prime(int x)
{
	if (x==1) return 0;
	for (int i=2;i*i<=x;++i) if (!(x%i)) return 0;
	return 1;
}

bool hungary(int x)
{
	if (vis[x]) return 0;
	vis[x]=1;
	for (int i=last[x],y;i;i=nxt[i])
		if (!bel[y=tov[i]]||hungary(y))
			return bel[y]=x;
	return 0;
}

