bool pairUp (int nod)
{
    if (C[nod]) return 0;
    C[nod] = 1;
    for (auto i : v[nod])
        if (lft[i] == 0 || pairUp (lft[i]))
        {
            lft[i] = nod, rgt[nod] = i;
            return 1;
        }
    return 0;
}

int maxMatch ()
{
    bool ok = 1;
    while (ok)
    {
        ok = 0;
        for (int i=1; i<=n; i++)
            C[i] = 0;
        for (int i=1; i<=n; i++)
            if (rgt[i] == 0)
                ok |= pairUp (i);
    }
    int ans = 0;
    for (int i=1; i<=n; i++)
        ans += (rgt[i] > 0);
    return ans;
}

