bool is_prime(int x){
	if(x==1)return 0;
	for(int i=2;i*i<=x;++i)if(x%i==0)return 0;
	return 1;
}

void ins(int x){
	if(x&1){if(L.count(x))L.erase(x);else L.insert(x);}
	else{if(R.count(x))R.erase(x);else R.insert(x);}
}

int dfs(int x){
	for(int i=1;i<=nr;++i)if(!vis[i]&&g[x][i]){
		vis[i]=1;
		if(!mL[i]||dfs(mL[i])){mL[i]=x;mR[x]=i;return 1;}
	}
	return 0;
}

