const int N = 1103;
const int X = 1e7 + 100;
const int INF = 0x3f3f3f3f;

int n, x[N];
bool mark[X + 10];
int cnt, prime[X];
bool notprime[X + 10];
int top, fi[N], ne[N * N], to[N * N], fl[N * N];
