    if (!notprime[i]) prime[++cnt] = i;
    for (int j = 1; j <= cnt; ++j) {
      if (prime[j] * i > X) break;
      notprime[prime[j] * i] = true;
      if (!(i % prime[j])) break;
    }
  }
  for (int i = read(); i >= 1; --i) mark[read()] = true;
  for (int i = 1; i <= X + 1; ++i) {
    if (mark[i] ^ mark[i - 1]) x[++n] = i;
  }
  S = n + 1, T = n + 2;
  int oddNum = 0, evenNum = 0;
  for (int i = 1; i <= n; ++i) {
    if (!(x[i] % 2)) add(i, T, 1), evenNum++;
    else add(S, i, 1), oddNum++;
  }
  for (int i = 1; i <= n; ++i) {
    if (!(x[i] % 2)) continue;
    for (int j = 1; j <= n; ++j) {
      if (x[j] % 2 || notprime[abs(x[i] - x[j])]) continue;
      add(i, j, 1);
      // printf("%d %d %d\n", x[i], x[j], x[i] - x[j]);
    }
  }

  /*
  for (int i = 1; i <= n; ++i) printf("%d ", x[i]);
  puts("");
  */

