#include<bits/stdc++.h>
using namespace std;

int V, E, src, snk;
vector<int> la, nxt, oppo, capa;
void init() {
    E = 0;
    la.clear(); nxt.clear(); oppo.clear(); capa.clear();
    la = vector<int>(V, -1);
}
void add(int u, int v, int c) {
    nxt.push_back(la[u]);
    la[u] = E++;
    oppo.push_back(v);
    capa.push_back(c);
}
vector<int> dist;
queue<int> q;
bool bfs() {
    dist = vector<int>(V, -1);
    q.push(src);
    dist[src] = 0;
    while(!q.empty()) {
        int u = q.front(); q.pop();

        for(int i = la[u]; i != -1; i = nxt[i]) {
            int v = oppo[i];
            if(capa[i] && dist[v] == -1) {
                q.push(v);
                dist[v] = dist[u] + 1;
            }
        }
    }
    return dist[snk] != -1;
}
vector<int> laa;
int dfs(int u, int f) {
    if(u == snk) return f;
    for(int i = laa[u]; i != -1; i = nxt[i]) {
        laa[u] = i;
        int v = oppo[i];
        if(capa[i] && dist[v] == dist[u] + 1) {
            if(int tmp = dfs(v, min(capa[i], f))) {
                capa[i] -= tmp;
                capa[i^1] += tmp;
                return tmp;
            }
        }
    }
    return 0;
}
int dinic() {
    int tf = 0;
    while(bfs()) {
        laa = la;
        while(int tmp = dfs(src, 1e9)) tf += tmp;
    }
    return tf;
}

vector<int> pchk;
void getPrime() {
    pchk = vector<int>(10000010, 0);
    for(int i = 2; i < 10000010; i++) {
        if(pchk[i]) continue;
        int mul = 2;
        while(i * mul < 10000010) {
            pchk[i * mul] = 1;
            mul++;
        }
    }
    pchk[1] = 1;
    pchk[2] = 1;
}

int N;
vector<int> X;
vector<int> B;
vector<int> Me, Mo;

int main() {
    getPrime();

    scanf("%d", &N);
    X.resize(N);
    for(int i = 0; i < N; i++) {
        scanf("%d", &X[i]);
    }
    for(int i = 0; i < N; i++) {
        if(i == 0 || X[i] != X[i - 1] + 1) {
            B.push_back(X[i]);
        }
        if(i == N - 1 || X[i] + 1 != X[i + 1]) {
            B.push_back(X[i] + 1);
        }
    }
    for(int i = 0; i < B.size(); i++) {
        if(B[i] % 2) Mo.push_back(B[i]);
        else Me.push_back(B[i]);
    }

    V = Me.size() + Mo.size() + 2, src = V - 2, snk = V - 1;
    init();

    for(int i = 0; i < Me.size(); i++) {
        add(src, i, 1);
        add(i, src, 0);
    }
    for(int i = 0; i < Mo.size(); i++) {
        add(Me.size() + i, snk, 1);
        add(snk, Me.size() + i, 0);
    }
    for(int i = 0; i < Me.size(); i++) {
        for(int j = 0; j < Mo.size(); j++) {
            if(!pchk[ abs(Me[i] - Mo[j]) ]) {
                add(i, Me.size() + j, 1);
                add(Me.size() + j, i, 0);
            }
        }
    }
    int K = dinic();
    cout<<K + (((int)Me.size() - K)/2 + ((int)Mo.size() - K)/2)*2 + (Me.size() % 2)*3;
}

