void add_edge(int from,int to){
	edge[++num_edge].nxt=head[from];
	edge[num_edge].to=to;
	head[from]=num_edge;
}
bool find(int x){
	for(int i=head[x];i;i=edge[i].nxt){
		if(vis[edge[i].to]!=tim){
			vis[edge[i].to]=tim;
			if(!lk[edge[i].to]||find(edge[i].to)){
				lk[edge[i].to]=x;
				return 1;
			}
		}
	}
	return 0;
}
bool check(int x){
	if(x==1) return 0;
	for(int i=2;i*i<=x;++i){
		if(x%i==0) return 0;
	}
	return 1;
}
