#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const int max_n=205;
const int max_m=10005;
struct edge{
    int u,v,next;
}G[max_m*2];
int head[max_n],tot;
void init(int n){
    tot=0;
    for(int i=0;i<=n;i++)head[i]=-1;
}
int match[max_n];
bool used[max_n];
void add_edge(int u,int v){
    G[tot]={u,v,head[u]};
    head[u]=tot++;
}
bool dfs(int u){
    used[u]=true;
    for(int i=head[u];~i;i=G[i].next){
        int v=G[i].v,w=match[v];
        if(w<0||!used[w]&&dfs(w)){
            match[u]=v,match[v]=u;
            return true;
        }
    }
    return false;
}
int solve(int n,int m){
    int res=0;
    for(int i=0;i<=n+m;i++)match[i]=-1;
    for(int u=1;u<=n;u++){
        if(match[u]==-1){
            for(int i=0;i<=n+m;i++)used[i]=false;
            if(dfs(u))res++;
        }
    }
    return res;
}
int n,a[max_n];
bool vis[10000005];
bool p[10000005];
int pri[10000005],cnt;
void sieve(int n){
    p[0]=p[1]=true;
    for(int i=2;i<=n;i++){
        if(!p[i])pri[++cnt]=i;
        for(int j=1;j<=cnt&&1ll*i*pri[j]<=n;j++){
            p[i*pri[j]]=true;
            if(i%pri[j]==0)break;
        }
    }
}
int main(){
    scanf("%d",&n);
    for(int i=1;i<=n;i++)scanf("%d",a+i);
    for(int i=1;i<=n;i++)vis[a[i]]^=1,vis[a[i]+1]^=1;
    sieve(10000000);
    vector<int> odd,even;
    for(int i=1;i<=10000001;i++){
        if(vis[i]){
            if(i&1)odd.push_back(i);
            else even.push_back(i);
        }
    }
    int nn=odd.size(),mm=even.size();
    init(nn+mm);
    for(int i=0;i<nn;i++){
        for(int j=0;j<mm;j++){
            if(!p[abs(even[j]-odd[i])]){
                add_edge(i+1,j+1+nn),add_edge(j+1+nn,i+1);
            }
        }
    }
    int x=solve(nn,mm);
    int ans=x+(nn-x)/2*2+(mm-x)/2*2+nn%2*3;
    printf("%d\n",ans);
    return 0;
}

