struct edge{
    int r,nxt,w;
}e[maxm];
int head[maxn],esz=1,S,T,dep[maxn],q[maxn],ql,qr,cur[maxn];
void addedge(int u,int v,int w){
//	printf("[%d->%d]\n",u,v);
    e[++esz].r=v;
    e[esz].nxt=head[u];
    e[esz].w=w;
    head[u]=esz;
    e[++esz].r=u;
    e[esz].nxt=head[v];
    e[esz].w=0;
    head[v]=esz;
}
bool bfs(){
    ql=qr=0;
    q[qr++]=S;
//	printf("[ok,%d,%d]",S,T);
    for(int i=S;i<=T;++i)dep[i]=0,cur[i]=head[i];
    dep[S]=1;
    while(ql<qr){
        int x=q[ql++];
        for(int t=head[x];t;t=e[t].nxt)if(!dep[e[t].r]&&e[t].w)
            dep[e[t].r]=dep[x]+1,q[qr++]=e[t].r;
    }
    return dep[T]!=0;
}
int find(int u,int flow){
    if(u==T)return flow;
    int a=0,used=0;
    for(int& t=cur[u];t;t=e[t].nxt)if(e[t].w&&dep[e[t].r]==dep[u]+1&&(a=find(e[t].r,min(flow-used,e[t].w)))){
        e[t].w-=a;e[t^1].w+=a,used+=a;
        if(used==flow)return used;
    }
    if(!used)dep[u]=0;
    return used;
}
