#define PNT_NO_START    (0)
#define PNT_NO_OFFSET   (200)
#define PNT_NO_TARGET   (401)
#define FLOW_OFFSET     (1000000000)
#define CAPACITY_INF    (1000000001)
#define DUMMY_COST      (1)

int main(void)
{
    SQWORD sqN = inputSQWORD();

    Flow solverFlow;
    vector<SQWORD> vsqA;
    vector<SQWORD> vsqB;
    vector<SDWORD> vlPrimes;

    getPrimes(vlPrimes, 1e7 + 1);
    vlPrimes.erase(find(vlPrimes.begin(), vlPrimes.end(), 2));

    for (SQWORD sqIdx = 0; sqIdx < sqN; sqIdx++) {
        SQWORD sqA = inputSQWORD();
        vsqA.emplace_back(sqA);
    }

#if 1
    for (auto it = vsqA.begin(); it != vsqA.end(); ++it) {
//        printf("%lld %lld\n", *it, *(it - 1));
        if (it == vsqA.begin()) {
            vsqB.emplace_back(*it);
        } else {
            if ((*it) - 1 != *(it - 1)) {
                vsqB.emplace_back(*it);
            }
        }
        if (it == vsqA.end() - 1) {
            vsqB.emplace_back((*it) + 1);
        } else {
            if ((*it) + 1 != *(it + 1)) {
                vsqB.emplace_back((*it) + 1);
            }
        }
    }
#else
    vsqB = vsqA;
#endif

    vector<SQWORD> vsqCOdd;
    vector<SQWORD> vsqCEven;

    unordered_map<SQWORD, SQWORD> mapCEven, mapCOdd;

    SQWORD sqIdx = 1;
    for (auto b: vsqB) {
        if (0 == b % 2) {
            vsqCEven.emplace_back(b);
            mapCEven[b] = sqIdx;
        } else {
            vsqCOdd.emplace_back(b);
            mapCOdd[b] = sqIdx;
        }
        sqIdx++;
    }

    /*  */
    for (auto c: vsqCEven) {
        solverFlow.addEdge(PNT_NO_START, mapCEven[c], 1, DUMMY_COST);
    }
    for (auto c: vsqCEven) {
        solverFlow.addEdge(mapCOdd[c] + PNT_NO_OFFSET, PNT_NO_TARGET, 1, DUMMY_COST);
    }

    for (auto p: vlPrimes) {
        for (auto c: vsqCEven) {
            auto it = lower_bound(vsqCOdd.begin(), vsqCOdd.end(), c + p);
            if (it != vsqCOdd.end()) {
                if (*it == c + p) {
                    if (c + p < 1e7 + 1) {
                        solverFlow.addEdge(mapCEven[c], mapCOdd[c + p] + PNT_NO_OFFSET, 1, DUMMY_COST);
                    }
                }
            }
        }
    }

    SQWORD sqMaxFlow = solverFlow.MaxFlow(PNT_NO_START, PNT_NO_TARGET);
    SQWORD vsqCRestEven = 0;
    SQWORD vsqCRestOdd = 0;
    for (auto c: vsqCEven) {
        SQWORD sqFlow = solverFlow.getFlow(mapCEven[c], true);
        if (0 == sqFlow) {
            vsqCRestEven++;
        }
    }
    for (auto c: vsqCOdd) {
        SQWORD sqFlow = solverFlow.getFlow(mapCOdd[c] + PNT_NO_OFFSET, false);
        if (0 == sqFlow) {
            vsqCRestOdd++;
        }
    }

    SQWORD sqAns = sqMaxFlow;    
    sqAns += (vsqCRestEven / 2) * 2;
    sqAns += (vsqCRestOdd / 2) * 2;
    if (0 < (vsqCRestEven %  2)) {
        sqAns += 3;
    }

    printf("%lld\n", sqAns);

    return 0;
}
