struct bipartite_matching{
    int n, x, y;
    VVI e;
    VI match;
    vector<bool> used;

    void init(int a, int b){
        x = a;
        y = b;
        n = x + y;
        e.resize(n);
        match.resize(n);
        used.resize(n);
    }

    void add_edge(int u, int v){
        e[u].push_back(v + x);
        e[v + x].push_back(u);
    }

    bool dfs(int v){
        used[v] = true;
        for (int u : e[v]){
            int w = match[u];
            if (w < 0 || (!used[w] && dfs(w))){
                match[v] = u;
                match[u] = v;
                return true;
            }
        }
        return false;
    }

    int calc_matching(){
        int res = 0;
        fill(ALL(match), -1);
        REP(v,n){
            if (match[v] < 0){
                used.assign(n, 0);
                if (dfs(v)) res++;
            }
        }
        return res;
    }
};

bool isprime(int x){
    if (x <= 2) return false;
    for (int i = 2; i * i <= x; i++){
        if (x % i == 0) return false;
    }
    return true;
}

int main() {
    int n;
    cin >> n;
    VI a(n);
    REP(i,n) a[i] = in();
    set<int> st;
    REP(i,n){
        if (st.count(a[i])) st.erase(a[i]);
        else st.insert(a[i]);
        st.insert(a[i] + 1);
    }
    VI odd, even;
    for (int x : st){
        // cout << x << endl;
        if (x % 2) odd.push_back(x);
        else even.push_back(x);
    }
    int x = odd.size(), y = even.size();
    bipartite_matching bp;
    bp.init(x, y);
    REP(i,x) REP(j,y){
        if (isprime(abs(odd[i] - even[j]))) bp.add_edge(i, j);
    }
    int c = bp.calc_matching();
    x -= c;
    y -= c;
    int ans = c + x / 2 * 2 + y / 2 * 2 + 3 * (x % 2);
    cout << ans << endl;

    return 0;
}

