bool isprime(int x){
  int i = 2;
  int q = 1;
  if(x == 1){
    q = 0;
  }
  while(i*i <= x){
    if((x%i) == 0){
      q=0; 
    }
    i++;
  }
  return q;
}

int cost(int x){
  if(x <= 0){
    return 0;
  }
  if((x%2) == 0){
    return 2;
  }
  else{
    if(M[x] == 2){
      return 1;
    }
    else
    {
      return 3;
    }
    if(!M[x]){
      if(isprime(x)){
        return 1;
        M[x] = 2;
      }
      else{
        return 3;
        M[x] = 1;
      }

    }
  }
}

void solve(){

  cin>>n;
  for(int i=1; i<=n; ++i){
    cin>>A[i];
  }

  for(int i=1; i<=103; ++i){
    dp[i] = INF;
  }
  //cout<<n<<"\n";

  dp[0] = 0;

  for(int i=1; i<=n; ++i){
    for(int j=1; j<=i; ++j){

      int ss = cost(A[i] - A[j] + 1);
      for(int k=j+1; k<=i; ++k){
        ss += cost(A[k] - A[k-1] - 1);
      }

      dp[i] = min(dp[i], dp[j-1] + ss);
    }
    //cout<<dp[i]<<" "<<i<<"\n";
  }
  cout<<dp[n]<<"\n";

}


