    int u, v, c;
    bool operator < (Edge e) {
        return c < e.c;
    }   
};

const int INF = 1e9+7, C = 1e7+7;

bool p[C];

int get(int a, int b) {
    if (b < a)
        swap(a, b);

    if (b - a == 1)
        return INF;

    if ((b - a) % 2 == 0)
        return 2;
    else if (p[b-a])
        return 1;
    else
        return 3;
}

int cost[N][N];

