template <class T> using reversed_priority_queue = priority_queue<T, vector<T>, greater<T> >;
typedef long long ll;

template <class T> struct FordFulkerson {

    vector < pair < int,int > > maching;

    struct  Edge {
        int to,rev; T cap;
        Edge(int t, int r, T c) : to(t), rev(r), cap(c) {}
    };

    const T INF = numeric_limits<T>::max() / 2;
    vector< vector< Edge > > G;
    vector< bool > used;
    FordFulkerson (int n) : G(n+1), used(n+1) {}

    void add_edge(int from, int to, T cap) {
        G[from].push_back(Edge(to,(int)G[to].size(),cap));
        G[to].push_back(Edge(from,(int)G[from].size()-1,0));
    }

    T dfs(int v, int t, T f) {
        if (v == t) return f;
        used[v] = true;
        for (int i = 0; i < G[v].size(); i++) {
            Edge &e = G[v][i];
            if (!used[e.to] && e.cap > 0) {
                T d = dfs(e.to, t, min(f,e.cap));
                if (d > 0) {
                    if (v != 990 && e.to != 991) {
                        maching.push_back(make_pair(v, e.to - 500));
                    }
                    e.cap -= d;
                    G[e.to][e.rev].cap += d;
                    return d;
                }
            }
        }
        return 0;
    }

    T get_max (int source, int sink) {
        T flow = 0;
        while (true) {
            for (int i = 0; i < used.size(); i++) used[i] = false;
            T f = dfs(source, sink, INF);
            if (f == 0) return flow;
            flow += f;
        }
    }

};


