	public:
	vi match,used;
	bool BM_dfs(int v){
		used[v]=1;
		for(auto u:g[v]){
			int w=match[u];
			if(w<0||!used[w]&&BM_dfs(w)){
				match[v]=u;
				match[u]=v;
				return 1;
			}
		}
		return 0;
	}
	int BM(){
		int res=0;
		match=vi(n,-1);
		for(int i=0;i<n;i++) if(match[i]<0){
			used=vi(n);
			if(BM_dfs(i)) res++;
		}
		return res;
	}
	Graph(int v){
		n=v;
		g=vvi(v);
	}
	void add_edge(int s,int t){
		g[s].push_back(t);
		g[t].push_back(s);
	}
};

