inline int bfs(){
	queue<int>q; q.push(0);
	memset(lev, 0, sizeof(lev)); lev[0]=1;
	while(!q.empty()){
		int v=q.front(); q.pop();
		for(int i=p[v]; i; i=a[i].next)
			if(a[i].l&&!lev[a[i].y]){
				lev[a[i].y]=lev[v]+1;
				if(a[i].y==T)return 1;
				q.push(a[i].y);
			}
	}
	return 0;
}
inline int dfs(int x, int ss){
	if(x==T||!ss)return ss;
	int s1=ss;
	for(int i=q[x]; i&&ss; i=a[i].next)
		if(lev[a[i].y]==lev[x]+1){
			int tt=dfs(a[i].y, min(a[i].l, ss));
			ss-=tt; a[i].l-=tt; a[i^1].l+=tt; q[x]=i;
		}
	if(s1==ss)lev[x]=-1;
	return s1-ss;
}

