		s[i].clear(),w[i].clear(),back[i].clear();
}
void jiantu(int x,int y,int l)
{
    s[x].push_back(y);
    s[y].push_back(x);
    w[x].push_back(l);
    w[y].push_back(0);
    back[x].push_back(s[y].size()-1);
    back[y].push_back(s[x].size()-1);
}
int bfs(int st,int en)
{
    int h=0,t=1,x,c,i,y;
    memset(cur,0,sizeof(cur));
    memset(d,0,sizeof(d));
    memset(vis,0,sizeof(vis));
    vis[st]=1,q[h]=st;
    while(h<t)
    {
        x=q[h++];
        if(x==en)
            continue;
        c=s[x].size();
        for(i=0;i<c;i++)
        {
            y=s[x][i];
            if(w[x][i]==0||vis[y]==1)	
                continue;
            vis[y]=1;
            d[y]=d[x]+1;
            q[t++]=y;
        }
    }
    return d[en];
}
int dfs(int x,int en,int f)
{
    if(x==en) return f;
    int tmp,ans=0,c,i,y;
    c=s[x].size();
    for(i=cur[x];i<c;i++)
    {
        cur[x]=i;
        y=s[x][i];
        if(w[x][i]==0||d[y]!=d[x]+1)
            continue;
        tmp=dfs(y,en,min(f-ans,w[x][i]));
        ans+=tmp;
        w[x][i]-=tmp;
        w[y][back[x][i]]+=tmp;
        if(ans==f)
            return ans;
    }
    d[x]=-1;
    return ans;
}
int dinic(int st,int en)
{
    int ans=0;
    while(bfs(st,en))
        ans+=dfs(st,en,INF);
    return ans;
}
