{
	int i,j;
	notprime[1]=1;
	for(i=2;i<=n;i++)
	{
		if(!notprime[i])
			pri[++cntt]=i;
		for(j=1;j<=cntt;j++)
		{
			if(i*pri[j]>n)
				break;
			notprime[i*pri[j]]=1;
			if(i%pri[j]==0)
				break;
		}
	}
}
