{
	if (p==t || !a) return a;
	int flow=0,f;
	for (int &i=cur[p];~i;i=e[i].nxt)
	if (d[e[i].to]==d[p]+1 && e[i].capa>e[i].flow)
	{
		f=dfs(e[i].to,min(a,e[i].capa-e[i].flow));
		if (f)
		{
			flow+=f; a-=f;
			e[i].flow+=f; e[i^1].flow-=f;
		}
		if (!a) break;
	}
	if (a) d[p]=-1;
	return flow;
}

