inline void add(int u, int v) {
	e[++tot] = (E){v, h[u]}, h[u] = tot;
	e[++tot] = (E){u, h[v]}, h[v] = tot;
}

template <class T> inline void in(T &x) {
	x = 0; int f = 1; char ch = getchar();
	for (; ch<'0' || ch>'9';) {if (ch=='-') f=-1; ch = getchar();}
	for (; ch>='0' && ch<='9';) x = x*10 + ch-'0', ch = getchar();
	x *= f;
}

inline void init() {
	for (int i = 3; i <= M; i += 2) p[i] = 1;
	for (int i = 3; i * i <= M; i += 2) {
		if (!p[i]) continue;
		for (int j = i * i; j <= M; j += i) p[j] = 0;
	}
}

inline int dfs(int x) {
	int v; used[x] = 1;
	for (int v, i = h[x]; i; i = e[i].nt)
		if (!mask[v = e[i].v] || (!used[mask[v]] && dfs(mask[v]))) {
			mask[v] = x, mask[x] = v; return 1;
		}
	return 0;
}

inline int match() {
	int res = 0;
	fill(mask + 1, mask + c + 1, 0);
	for (int i = 1; i <= c; ++i)
		if (!mask[i]) {
			fill(used + 1, used + c + 1, 0);
			if (dfs(i)) ++res;
		}
	return res;
}

inline void work() {
	int val; in(n);
	for (int i = 1; i <= n; ++i) {
		in(val);
		if (!c || val != x[c]) x[++c] = val;
		else --c;
		x[++c] = val + 1;
	}
	for (int i = 1; i <= c; ++i) if (x[i] % 2 == 0) ++cnt;
	for (int i = 1; i <= c; ++i)
		for (int j = i + 1; j <= c; ++j)
			if (p[x[j] - x[i]]) add(i, j);
	int ret = match();
	printf("%d\n", c / 2 + (c / 2 - ret) + (cnt - ret) % 2);
}

