	for (auto u : S) for (auto v : T) if (!vis[abs(x[u] - x[v])]) V[u].emplace_back(v);
	for (auto u : S) if (memset(use, 0, sizeof use), find(u)) ++k;
	printf("%d\n", (S.size() - k) / 2 * 2 + (T.size() - k) / 2 * 2 + (S.size() & 1) * 3 + k);
	return 0;
}

#include<bits/stdc++.h>
